function factorialize(num) {
  return num < 1 ? 1 : num * factorialize(num-1);
}

factorialize(5);

function palindrome(str) {
  var string = str.toLowerCase().split(/[^A-Za-z0-9]/gi).join('');
  var aux = string.split('');
  if (aux.join('') === aux.reverse().join('')){
    return true;
  }

  return false;
}

function findLongestWord(str) {
  return str.split(' ').sort(function(a, b) { return b.length - a.length;})[0].length;
}

findLongestWord('The quick brown fox jumped over the lazy dog');

function titleCase(str) {
  return str.split(' ').map(function(word) {
    return word.charAt(0).toUpperCase() + word.substring(1).toLowerCase();
  }).join(' ');
}

titleCase("I'm a little tea pot");

function largestOfFour(arr) {
  return arr.map(function(subArr) {
    return Math.max.apply(null, subArr);
  });
}

largestOfFour([[4, 5, 1, 3], [13, 27, 18, 26], [32, 35, 37, 39], [1000, 1001, 857, 1]]);

function confirmEnding(str, target) {
  return str.substring(str.length-target.length) === target;
};

function repeat(str, num) {
  if (num < 0) return '';
  return num === 1 ? str : str + repeat(str, num-1);
}

repeat('abc', 3);

function truncate(str, num) {
  if(str.length > num ) {
    if(num > 3) {
      return str.slice(0, num - 3) + '...';
    } else {
      return str.slice(0,num) + '...';
    }
  } 
  return str;
}

function chunk(arr, size) {
  var out = [];
  for (var i = 0; i < arr.length; i+=size) {
    out.push(arr.slice(i,i+size));
  }
  return out;
}

chunk(['a', 'b', 'c', 'd'], 2);

function slasher(arr, howMany) {
  // it doesn't always pay to be first
  return arr.slice(howMany);
}

slasher([1, 2, 3], 2);

function mutation(arr) {
  var hash = Object.create(null);
  arr[0].toLowerCase().split('').forEach(function(c) {
    hash[c] = true;
  });
  return !arr[1].toLowerCase().split('').filter(function(c) {
    return !hash[c];
  }).length;
}

mutation(['hello', 'hey']);

function bouncer(arr) {
  // Don't show a false ID to this bouncer.
  return arr.filter(function(e) {return e;});
}

bouncer([7, 'ate', '', false, 9]);

function destroyer(arr) {
  var hash = Object.create(null);
  [].slice.call(arguments, 1).forEach(function(e) {
    hash[e] = true;
  });
  // Remove all the values
  return arr.filter(function(e) { return !(e in hash);});
}

destroyer([1, 2, 3, 1, 2, 3], 2, 3);

function where(arr, num) {
  arr = arr.sort(function(a, b){return a-b;});
  for (var i = 0; i < arr.length; i++) {
    if (arr[i] >= num)
      {
        return i;
      }
  }
  return arr.length;
}

