var Car = function() {
  var speed = 10;

  this.accelerate = function(change) {
    speed += change;
  };

  this.decelerate = function() {
    speed -= 5;
  };

  this.getSpeed = function() {
    return speed;
  };
};

var Bike = function() {
  var gear = 1;

  this.getGear = function() {
    return gear;
  };

  this.setGear = function(newGear) {
    gear = newGear;
  };
};

var myCar = new Car();

var myBike = new Bike();
